import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import SessionService from "../../../services/SessionService";

export default function MovieSessions() {
    const { movieId } = useParams();
    const [sessions, setSessions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchSessions = async () => {
            try {
                const data = await SessionService.getAllSessionsFromMovie(movieId);
                setSessions(data);
            } catch (err) {
                setError("Oturumlar yüklenirken bir hata oluştu.");
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchSessions();
    }, [movieId]);

    if (loading) return <div className="p-4">Yükleniyor...</div>;
    if (error) return <div className="p-4 text-red-600">{error}</div>;

    return (
        <div className="p-6">
            <h2 className="text-xl font-bold mb-4">Film Oturumları</h2>
            {sessions.length === 0 ? (
                <p>Bu filme ait oturum bulunamadı.</p>
            ) : (
                <ul className="space-y-2">
                    {sessions.map((session) => (
                        <li key={session.id} className="bg-white p-4 rounded shadow">
                            <p><strong>Başlangıç:</strong> {session.startTime}</p>
                            <p><strong>Bitiş:</strong> {session.endTime}</p>
                            <p><strong>Salon:</strong> {session.hall?.name || "Bilinmiyor"}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
