import { create } from 'zustand'

const useAuth = create((set) => ({
    user: null,
    role: null,
    error: null,

    loginUser: async (credentials) => {
        try {
            return true
        } catch (err) {
            set({ error: err.response?.data?.message || 'Login failed' })
            return false
        }
    },

    logout: () => set({ user: null, role: null }),
}))

export default useAuth
