import { useNavigate } from "react-router-dom";
import useAuth from "../store/useAuth";
import { useState } from "react";

export default function LoginPage() {
    const { setUser, setRole } = useAuth();
    const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = () => {
        if (email && password) {

            const role = email === "admin@cms.com" ? "admin" : "clerk";

            setUser({ name: email });
            setRole(role);

            navigate(role === "admin" ? "/admin" : "/clerk");
        }
    };

    return (
        <div className="login font-[Poppins] h-[100%]c">
            <div className="bg-[#400505] h-[20%]c flex items-center">
                <h1 className="text-white font-normal ml-5">Cinema Management System</h1>
            </div>
            <hr />
            <div className="bg-[#202123]">

                <div className="form"></div>

                <div className="footer">

                </div>
            </div>

            <div className="min-h-screen flex items-center justify-center bg-gray-100">
                <div className="bg-white p-8 rounded shadow-md w-full max-w-md space-y-4">
                    <h2 className="text-2xl font-semibold text-center">Login</h2>

                    <input
                        type="email"
                        placeholder="E-mail"
                        className="w-full border p-2 rounded"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />

                    <input
                        type="password"
                        placeholder="Password"
                        className="w-full border p-2 rounded"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />

                    <button
                        className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
                        onClick={handleLogin}
                    >
                        Login
                    </button>
                </div>
            </div>
        </div>
    );
}
