import { useNavigate } from "react-router-dom";
import useAuth from "../store/useAuth";
import { useState } from "react";

export default function LoginPage() {
    const { setUser, setRole } = useAuth();
    const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = () => {
        if (email && password) {

            const role = email === "admin@cms.com" ? "admin" : "clerk";

            setUser({ name: email });
            setRole(role);

            navigate(role === "admin" ? "/admin" : "/clerk");
        }
    };

    return (
        <div className="login h-screen font-[Poppins]">
            <div className="bg-[#400505] h-5 items-center">
                <h1 className="text-white font-normal ml-5">Cinema Management System</h1>
            </div>
            <hr />
            <div className="bg-[#202123] h-screen">

                <div className="form"></div>

                <div className="footer">

                </div>
            </div>
        </div>
    );
}
