import Header from "../../components/Header";
import React from "react";
import ClerkSearchBookingTable from "../../components/ClerkSearchBookingTable";
import ClerkSidebar from "../../components/ClerkSidebar";
import SearchBar from "../../components/AdminCustomersSearchBar";

export default function ClerkSession({movie}){
    return (
        <div className="h-screen flex font-[Poppins]">
            <ClerkSidebar />
            <div className="flex-1 flex flex-col">
                <Header title={movie.name + " Sessions"} />
                <main className="inner-container relative flex-1 p-10 bg-[#D9D9D9]">
                    <div className="bg-white w-full h-full mx-3 my-4 rounded-xl p-6 overflow-auto grid grid-cols-3 gap-4 ">
                        <div className="bg-[#400505] h-full w-[80%] p-5 rounded-xl relative col-span-2 flex justify-items-center ml-8 grid grid-cols-2 gap-4">
                            {movie.sessions.map((session) => (
                                <div>
                                    <button className="bg-[#D9D9D9] rounded-md px-16 py-4 mt-6">
                                        {session.time}
                                    </button>
                                </div>
                            ))}
                        </div>
                        <div className="bg-[#D9D9D9] w-[100%] relative inline rounded-xl p-10 relative flex justify-items-center">
                                <img src={movie.img} className="rounded-md"/>
                                <label className="text-black font-bold">{movie.time} / {movie.category}</label>
                                <b>{movie.d}</b>
                        </div>
                    </div>
                </main>
            </div>
        </div>
    )
}
