import axios from '../../../lib/axios'

export const login = async ({ email, password }) => {
    try {
        const response = await axios.post('/auth/login', { email, password })
        return response.data
    } catch (error) {
        // Burada hatayı dışarı fırlat, zustand catch etsin
        throw error
    }
}
