// src/features/auth/pages/ClerkMoviesPage.jsx
import React, {useEffect, useState} from "react";
import ClerkSidebar from "../../../components/ClerkSidebar";
import Header from "../../../components/Header";
import Movie from "../../../components/ClerkMovies/Movie";
import ClerkMoviesService from "../../../services/MovieService";
import TicketClerkService from "../../../services/TicketClerkService";

export default function ClerkSettings({id}) {
    const[clerk, setClerk] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            TicketClerkService.getById(id)
                .then(data => setClerk(setClerk))
                .catch(error => console.log("Data couldnt fetch : " + error))
        }
        fetchData();
    }, []);

    const handleSubmit = () => {

    }


    return (
        <div className="h-screen flex">
            <ClerkSidebar />

            <div className="flex-1 flex flex-col">
                <Header title="Settings"/>

                <main className="flex-1 overflow-auto bg-[#D9D9D9] p-6">
                    <div className="bg-white rounded-xl p-6 grid grid-cols-4 gap-6">
                        <form className="p-8 space-y-4" onSubmit={handleInput}>
                            <label className="block text-black font-semibold">Hall Name</label>
                            <input
                                type="text"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                className="border-2 border-gray-300 rounded-md h-8 w-full"
                                required
                            />
                            <label className="block text-black font-semibold">Hall Capacity</label>
                            <input
                                type="text"
                                value={capacity}
                                onChange={(e) => setCapacity(e.target.value)}
                                className="border-2 border-gray-300 rounded-md h-8 w-full"
                                required
                            />
                            <label className="block text-black font-semibold">Hall Type</label>
                            <input
                                type="text"
                                value={type}
                                onChange={(e) => setType(e.target.value)}
                                className="border-2 border-gray-300 rounded-md h-8 w-full"
                                required
                            />
                            <button
                                type="submit"
                                className="bg-[#400505] text-white py-2 px-6 rounded-md"
                            >
                                Add Hall
                            </button>
                        </form>
                    </div>
                </main>
            </div>
        </div>
    );
}
