// src/features/auth/pages/ClerkMoviesPage.jsx
import React, {useEffect, useState} from "react";
import ClerkSidebar from "../../../components/ClerkSidebar";
import Header from "../../../components/Header";
import Movie from "../../../components/ClerkMovies/Movie";
import ClerkMoviesService from "../../../services/MovieService";
import TicketClerkService from "../../../services/TicketClerkService";

export default function ClerkSettings({id}) {
    const[clerk, setClerk] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            TicketClerkService.getById(id)
                .then(data => setClerk(setClerk))
                .catch(error => console.log("Data couldnt fetch : " + error))
        }
        fetchData();
    }, []);



    return (
        <div className="h-screen flex">
            <ClerkSidebar />

            <div className="flex-1 flex flex-col">
                <Header title="Settings"/>

                <main className="flex-1 overflow-auto bg-[#D9D9D9] p-6">
                    <div className="bg-white rounded-xl p-6 grid grid-cols-4 gap-6">
                        <form className="p-8" onSubmit={handleSubmit}>
                            <label className="block text-black bold">Client Name</label>
                            <input type="text"
                                   value={clientName}
                                   onChange={(e) => setClientName(e.target.value)}
                                   className="border-2 border-gray-300 rounded-md h-8 w-full mt-0.5"
                                   required/>
                            <label className="block text-black mt-8">Movie</label>
                            <input type="text"
                                   value={movie}
                                   onChange={(e) => setMovie(e.target.value)}
                                   className="border-2 border-gray-300 rounded-md h-8 w-full  mt-0.5"
                                   required/>
                            <label className="block text-black mt-8">Hall</label>
                            <input type="text"
                                   value={hall}
                                   onChange={(e) => setHall(e.target.value)}
                                   className="border-2 border-gray-300 rounded-md h-8 w-full  mt-0.5"
                                   required/>
                            <label className="block text-black mt-8">Description</label>
                            <textarea type="text"
                                      value={description}
                                      rows="3"
                                      onChange={(e) => setDescription(e.target.value)}
                                      className="border-2 border-gray-300 rounded-md h-24 w-full mt-0.5"
                                      required/>
                            <button type="submit" className="mt-12 bg-[#400505] text-white py-2 px-16 rounded-xl">Submit</button>
                        </form>
                    </div>
                </main>
            </div>
        </div>
    );
}
